                    </div>
                    <div class=\"d-flex justify-content-center mt-3\">
                        <button class=\"btn btn-info\">Envoyer un email de réinitialisation de mot de passe</button>
                    </div>
                {{ form_end(requestForm) }}
            </section>
        </article>
    </main>
